import React from 'react'
import "./Login.css"
import $ from 'jquery';
import { initializeApp } from "https://www.gstatic.com/firebasejs/9.6.6/firebase-app.js";
import { getAnalytics } from "https://www.gstatic.com/firebasejs/9.6.6/firebase-analytics.js";
import { getDatabase, ref, det, child, get} 
    from "https://www.gstatic.com/firebasejs/9.6.6/firebase-app.js";
$(()=>{
    // Login Register Form
    $('#logreg-forms #forgot_pswd').click(toggleResetPswd);
    $('#logreg-forms #cancel_reset').click(toggleResetPswd);
    $('#logreg-forms #btn-signup').click(toggleSignUp);
    $('#logreg-forms #cancel_signup').click(toggleSignUp);
})

function toggleResetPswd(e){
    e.preventDefault();
    $('#logreg-forms .form-signin').toggle() // display:block or none
    $('#logreg-forms .form-reset').toggle() // display:block or none
}

function toggleSignUp(e){
    e.preventDefault();
    $('#logreg-forms .form-signin').toggle(); // display:block or none
    $('#logreg-forms .form-signup').toggle(); // display:block or none
}







const firebaseConfig = {
  apiKey: "AIzaSyA4Ws3FboizSnh1oQx-L88SqlHs7umdmPk",
  authDomain: "soen-341-a3280.firebaseapp.com",
  databaseURL: "https://soen-341-a3280-default-rtdb.firebaseio.com",
  projectId: "soen-341-a3280",
  storageBucket: "soen-341-a3280.appspot.com",
  messagingSenderId: "878236206473",
  appId: "1:878236206473:web:78b86cacc5bf537ed34d58",
  measurementId: "G-SSWF20N4TB"
};

const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

const db = getDatabase();


const name = document.getElementById('name');
const user = document.getElementById('user-name');
const email = document.getElementById('user-email');
const pass = document.getElementById('user-pass');
const submit = document.getElementById('sub_btn');

function Validation() {
    let nameregex = /^[a-zA-Z]+$/;
    let email = /^[a-zA-Z0-9]+@(gmail|yahoo|outlook)\.com$/;
    let userregex = /^[a-zA-Z0-9]{4,}$/;
    if(!nameregex.text(name.value)) {
        alert("The name should only contain alphabets.");
        return false;
    }
    if(!emailregex.text(email.value)) {
        alert("Enter a valid email.");
        return false ;
    }
    if(!userregex.text(user.value)) {
        alert("user name can only contain alpahebts and number. \n User must be at least 4 characters.");
        return false;
    }
    return true;
}
function RegisterUser() {
    if(!Validation()) {
        return;
    };
    const dbRef = ref(db);

    get(child(dbRef, "UserList/" + user.value)).then((snapshot)=>{
        if(snapshot.exists()) {
            alert("User already taken.");
        }
        else {
            set(ref(db, "UserList/" + user.value), 
            {
                fullname : name.value,
                email : email.value,
                user: user.value,
                password: pass.value
            })
            .then(() => {
                alert("user added!");
            })
            .catch((error) => {
                alert("error: " + error);
            });
        }
    });  
}
submit.addEventListener('click', RegisterUser)

function Login() {
 
    return (
    <div className="login">
                <div id="container">

        
 <div id="logreg-forms">
        <form class="form-signin"/>
            <h1 class="h3 mb-3 font-weight-normal" style="text-align: center"> Sign in</h1>
            <div class="social-login">
                <button class="btn facebook-btn social-btn" type="button"><span><i class="fab fa-facebook-f"></i> Sign in with Facebook</span> </button>
                <button class="btn google-btn social-btn" type="button"><span><i class="fab fa-google-plus-g"></i> Sign in with Google+</span> </button>
            </div>
            <p style="text-align:center"> OR  </p>
            <input type="email" id="inputEmail" class="form-control" placeholder="Email address" required="" autofocus=""/>
            <input type="password" id="inputPassword" class="form-control" placeholder="Password" required=""/>
            
            <button class="btn btn-success btn-block" type="submit"><i class="fas fa-sign-in-alt"></i> Sign in</button>
            <a href="#" id="forgot_pswd">Forgot password?</a>
            <hr/>
           
            <button class="btn btn-primary btn-block" type="button" id="btn-signup"><i class="fas fa-user-plus"></i> Sign up New Account</button>
  

            <form action="/reset/password/" class="form-reset">
                <input type="email" id="resetEmail" class="form-control" placeholder="Email address" required="" autofocus=""/>
                <button class="btn btn-primary btn-block" type="submit">Reset Password</button>
                <a href="#" id="cancel_reset"><i class="fas fa-angle-left"></i> Back</a>
            </form>
            
            <form action="" class="form-signup"/>
                <div class="social-login">
                    <button class="btn facebook-btn social-btn" type="button"><span><i class="fab fa-facebook-f"></i> Sign up with Facebook</span> </button>
                </div>
                <div class="social-login">
                    <button class="btn google-btn social-btn" type="button"><span><i class="fab fa-google-plus-g"></i> Sign up with Google+</span> </button>
                </div>
                
                <p style="text-align:center">OR</p>

                <input type="text" id="name" class="form-control" placeholder="Full name"  autofocus="" name="name"/>
                <input type="text" id="user-name" class="form-control" placeholder="User name"  autofocus="" name="user"/>
                <input type="email" id="user-email" class="form-control" placeholder="Email address"  autofocus="" name="email"/>
                <input type="password" id="user-pass" class="form-control" placeholder="Password"  autofocus="" name="pass"/>
                <input type="password" id="user-repeatpass" class="form-control" placeholder="Confirm Password"  autofocus="" name="pass"/>

                <button class="btn btn-primary btn-block" type="submit" id="sub_btn" name="submit"><i class="fas fa-user-plus"></i> Sign Up</button>
                <a href="#" id="cancel_signup"><i class="fas fa-angle-left"></i> Back</a>
            <br/>
            
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js"></script>
    







      <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/js/bootstrap.bundle.min.js" integrity="sha384-ka7Sk0Gln4gmtz2MlQnikT1wXgYsOg+OMhuP+IlRH9sENBO0LRn5q+8nbTov4+1p" crossorigin="anonymous"></script>
