export const initialState = {
    basket: [],
};

export const getBaskeTotal = (basket) =>
    basket?.reducer((amount, item) => item.price + amount, 0);

const reducer = (state,action) => {
    console.log(action);
        switch(action.type){
            case 'ADD_TO_BASKET':
                return{
                    ...state,
                    basket: [...state.basket, action.item],
                };

                default:
                    return state;
        }
};

export default reducer;
